// SPDX-License-Identifier: MIT
pragma solidity 0.8.25;

/// @title OFTComposeMsgCodec
/// @author Forked from
/// https://github.com/LayerZero-Labs/LayerZero-v2/blob/main/packages/layerzero-v2/evm/oapp/contracts/oft/libs/OFTComposeMsgCodec.sol
/// @dev Modifications made by Cooper Labs. Removed 'LD' (Local Decimals) related code.
library OFTComposeMsgCodec {
    // Offset constants for decoding composed messages
    uint8 private constant NONCE_OFFSET = 8;
    uint8 private constant SRC_EID_OFFSET = 12;
    uint8 private constant AMOUNT_OFFSET = 44;
    uint8 private constant COMPOSE_FROM_OFFSET = 76;

    /// @dev Encodes a OFT composed message.
    /// @param _nonce The nonce value.
    /// @param _srcEid The source endpoint ID.
    /// @param _amount The amount.
    /// @param _composeMsg The composed message.
    /// @return _msg The encoded Composed message.
    function encode(
        uint64 _nonce,
        uint32 _srcEid,
        uint256 _amount,
        bytes memory _composeMsg // 0x[composeFrom][composeMsg]
    )
        internal
        pure
        returns (bytes memory _msg)
    {
        _msg = abi.encodePacked(_nonce, _srcEid, _amount, _composeMsg);
    }

    /// @dev Retrieves the nonce from the composed message.
    /// @param _msg The message.
    /// @return The nonce value.
    function nonce(bytes calldata _msg) internal pure returns (uint64) {
        return uint64(bytes8(_msg[:NONCE_OFFSET]));
    }

    /// @dev Retrieves the source endpoint ID from the composed message.
    /// @param _msg The message.
    /// @return The source endpoint ID.
    function srcEid(bytes calldata _msg) internal pure returns (uint32) {
        return uint32(bytes4(_msg[NONCE_OFFSET:SRC_EID_OFFSET]));
    }

    /// @dev Retrieves the amount from the composed message.
    /// @param _msg The message.
    /// @return The amount.
    function amount(bytes calldata _msg) internal pure returns (uint256) {
        return uint256(bytes32(_msg[SRC_EID_OFFSET:AMOUNT_OFFSET]));
    }

    /// @dev Retrieves the composeFrom value from the composed message.
    /// @param _msg The message.
    /// @return The composeFrom value.
    function composeFrom(bytes calldata _msg) internal pure returns (bytes32) {
        return bytes32(_msg[AMOUNT_OFFSET:COMPOSE_FROM_OFFSET]);
    }

    /// @dev Retrieves the composed message.
    /// @param _msg The message.
    /// @return The composed message.
    function composeMsg(bytes calldata _msg) internal pure returns (bytes memory) {
        return _msg[COMPOSE_FROM_OFFSET:];
    }

    /// @dev Converts an address to bytes32.
    /// @param _addr The address to convert.
    /// @return The bytes32 representation of the address.
    function addressToBytes32(address _addr) internal pure returns (bytes32) {
        return bytes32(uint256(uint160(_addr)));
    }

    /// @dev Converts bytes32 to an address.
    /// @param _b The bytes32 value to convert.
    /// @return The address representation of bytes32.
    function bytes32ToAddress(bytes32 _b) internal pure returns (address) {
        return address(uint160(uint256(_b)));
    }
}

